"use strict";

var namesGenerator = require('./namesGenerator'),
    sortData = require('./sortData');

/**
 *
 * @param {Array} data
 * @returns {Object} 
 */
module.exports = function (data) {
    var new_class_names = {},
        sort_data = sortData(data),
        name = namesGenerator();


    for (var i = 0; i < sort_data.length; i++) {
        new_class_names[sort_data[i]] = name.generate();
    }
    return new_class_names;

};