"use strict";

/**
 *return sorted data in descending order for one copy
 * @param data
 * @returns {Array}
 */
module.exports = function (data) {

    var count_value = {},
        sort_value = [];

    function sorting(a, b) {
        return (count_value[a] - count_value[b])
    }

    for (var i = 0; i < data.length; i++) {
        if (~sort_value.indexOf(data[i])) {
            count_value[data[i]] ++;
        } else {
            sort_value.push(data[i]);
            count_value[data[i]] = 1;
        }
    }
    sort_value.sort(sorting).reverse();
    return sort_value;
};
