"use strict";

/**
 *return sorted data in descending order for one copy
 * @param data
 * @returns {Array}
 */
module.exports = function (data) {

    var count_value = {}, // в объекте будут храниться ключи и колличество каждого значения в массиве data
        sort_value = [];

     
    /**
    * метод по которому будет отсортирован массив с данными
    */
    function sorting(a, b) {
        return (count_value[a] - count_value[b])
    }
    
    /**
    * перебираю массив с данными data и добовляю одно новое значение массив
    * sort_value и в объект count_value добовляю этот ключ со значениev 1,
    * если в массиве sort_value уже есть такое значение , тогда в count_value 
    * добовляю +1
    *  
    */
    for (var i = 0; i < data.length; i++) { 
        if (~sort_value.indexOf(data[i])) { 
            count_value[data[i]] ++;
        } else {
            sort_value.push(data[i]);
            count_value[data[i]] = 1;
        }
    }
    sort_value.sort(sorting).reverse();
    return sort_value;
};
